import React, { useState } from 'react';
import { media_list } from "../data/media_list";

function NewContentCreation() {
  const [selectedSocialMedia, setSelectedSocialMedia] = useState('');
  const [selectedOption, setSelectedOption] = useState('');

  const handleSocialMediaChange = (event) => {
    setSelectedSocialMedia(event.target.value);
    setSelectedOption(''); // Reset the option when social media changes
  };

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  // Get the options for the selected social media
  const selectedSocialMediaOptions = media_list.find(media => media.name === selectedSocialMedia)?.options || [];

  return (
    <div className='create-wrapper'>
      <h1 className='create-title'>Create New Content</h1>

      <label className='create-selector'>
        Choose Social Media:
        <select value={selectedSocialMedia} onChange={handleSocialMediaChange}>
          <option value=''>Select...</option>
          {media_list.map((media) => 
            <option key={media.name} value={media.name}>{media.name}</option>
          )}
        </select>
      </label>

      {selectedSocialMedia && (
        <label className='create-selector'>
          Choose Option:
          <select value={selectedOption} onChange={handleOptionChange}>
            <option value=''>Select...</option>
            {selectedSocialMediaOptions.map((option) => 
              <option key={option} value={option}>{option}</option>
            )}
          </select>
        </label>
      )}

      {/* 
        Depending on your design you can show different forms/inputs based on the
        selected social media and option. You might need different components or 
        maybe a dynamic form
      */}
    </div>
  );
}

export default NewContentCreation;
