import React, { useState } from 'react';
import { media_list } from "../data/media_list";

function NewContentCreation() {
  const [selectedSocialMedia, setSelectedSocialMedia] = useState('');
  const [selectedOption, setSelectedOption] = useState('');

  const handleSocialMediaChange = (event) => {
    setSelectedSocialMedia(event.target.value);
    setSelectedOption(''); // Reset the option when social media changes
  };

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  // Get the options for the selected social media
  const selectedSocialMediaOptions = media_list.find(media => media.name === selectedSocialMedia)?.options || [];

  // handle FORM element
  const handleFormSubmit = (event) => {
    event.preventDefault();
    // Handle form submission here.
    // You might want to gather the data from the form and send it to your backend or directly post it to the social media API.
    console.log(`Creating new ${selectedOption} for ${selectedSocialMedia}`);
  };

  return (
    <div className='create-wrapper'>
      <h1 className='create-title'>Create New Content</h1>

      <label>
        Choose Social Media:
        <select className='create-selector' value={selectedSocialMedia} onChange={handleSocialMediaChange}>
          <option value=''>Select...</option>
          {media_list.map((media) => 
            <option key={media.name} value={media.name}>{media.name}</option>
          )}
        </select>
      </label>

      {selectedSocialMedia && (
        <label>
          Choose Option:
          <select className='create-selector' value={selectedOption} onChange={handleOptionChange}>
            <option value=''>Select...</option>
            {selectedSocialMediaOptions.map((option) => 
              <option key={option} value={option}>{option}</option>
            )}
          </select>
        </label>
      )}

      {/* Render dynamic form based on the selected social media and option */}
      {selectedSocialMedia && selectedOption && (
        <div>
          {/* Insert your form here based on selectedSocialMedia and selectedOption */}
          {/* You might use different components or different sets of inputs */}
          {/* This is a basic example, and it needs to be fleshed out based on your requirements */}
          <form>
            {/* The form fields would depend on the selected social media and the selected option */}
            {/* For example, for Instagram post you might need an image upload field, a caption field, etc. */}
            {/* For TikTok video, you might need a video upload field, a sound selection field, etc. */}
            {/* The actual implementation depends on your needs and the APIs provided by the social media platforms */}
            <input type="text" placeholder="Your content..." />
            <button type="submit">Create</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default NewContentCreation;
