import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const SinglePostEdit = () => {
  const [content, setContent] = useState(null);
  const [error, setError] = useState(null);

  const { id } = useParams(); // get the ID from the URL

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch(`http://localhost:3500/content/edit/${id}`);
        if (response.ok) {
          const data = await response.json();
          setContent(data);
        } else {
          setError('Failed to fetch content');
        }
      } catch (error) {
        setError('An error occurred while fetching content data');
      }
    };

    fetchUser();
  }, [id]);

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!content) {
    return <div>Loading...</div>;
  }



  return (
    <div className='user-wrapper'>
    <h2 className='user-name'>{content.title}</h2>
    <div className='user-details'>

      <p> <b>Id:</b> {content.username}</p>
      <p> <b>User Id:</b> {content.id}</p>
      <p> <b>Platform:</b> {content.platform}</p>
      <p> <b>Caption:</b> {content.caption}</p>
      <p contentEditable> <b>Description:</b> {content.description}</p>
    </div>
  </div>
  );
};

export default SinglePostEdit;
