      setDateFilter(event.target.value);
    };
  const handleStatusFilterChange = (event) => {
      setTypeFilter(event.target.value);
    };

  return (
    <div className='admin-wrapper'>
      <h1>User List</h1>
      {users.map((user) => (
        <div key={user.id}>
          <p>Name: {user.name}</p>
          <p>Username: {user.username}</p>
          <p>Status: {user.status}</p>
          <hr />
        </div>
      ))}
    </div>
  );
};

export default Admin;
