import React, { useState, useEffect } from 'react';

const Admin = () => {

  // Fetch DB JSON
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('http://localhost:3500/users'); // Replace with the actual API route for fetching users
        if (response.ok) {
          const data = await response.json();
          setUsers(data);
        } else {
          console.error('Failed to fetch users');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUsers();
  }, []);

  // Filters
  const [nameFilter, setNamelFilter] = useState(null);
  const [usernameFilter, setUsernameFilter] = useState(null);
  const [statusFilter, setStatusFilter] = useState(null);

  // Filters
  const handleNameFilterChange = (event) => {
    setNamelFilter(event.target.value);
    };
  const handleUsernameFilterChange = (event) => {
    setUsernameFilter(event.target.value);
    };
  const handleStatusFilterChange = (event) => {
    setStatusFilter(event.target.value);
    };

  return (
    <div className='admin-wrapper'>

        <aside className="filters">
            <h3>Filters</h3>
            <div className="filters-wrapper">

              <div className='filter-container'>
                    <label><b>Name:</b></label>
                    <input type="text" onChange={handleStatusFilterChange} placeholder='Type any word'/>
                </div>

                <div className='filter-container'>
                  <label><b>Username:</b></label>
                  <input type="text" onChange={handleStatusFilterChange} placeholder='Type any word'/>
                </div>

                <div className='filter-container'>
                  <label><b>Status:</b> </label>
                  <select onChange={handleNameFilterChange}>
                    <option value="">All</option>
                    <option value="master">Master</option>
                    <option value="user">User</option>
                    <option value="client">Client</option>
                  </select>
                </div>


            </div>
          </aside>


          <section>
            <h1>User List</h1>
            {users.map((user) => (
              <div key={user.id}>
                <p>Name: {user.name}</p>
                <p>Username: {user.username}</p>
                <p>Status: {user.status}</p>
                <hr />
              </div>
            ))}
          </section>
    </div>
  );
};

export default Admin;
